def makecolors(n=128):
        
    """产生颜色表，饱和度和亮度最高，所以很鲜艳"""
    cs = []
    for y in range(n):
        x = random.random()
        r,g,b = colorsys.hsv_to_rgb(x,1,1)
        r,g,b = int(r*255),int(g*255),int(b*255)
        cs.append((r,g,b))
    cs = ["#{:02x}{:02x}{:02x}".format(r,g,b) for r,g,b in cs]
    cs = list(set(cs))
    return cs
